function convertToWeight(weight_unit,weight)
{
	var value=weight;
	var grams;
	if(weight_unit=="lb")
	{
		grams=value/453.592;
	}
	else if(weight_unit=="stone")
	{
		grams=value/6350.28800006585;
	}
	else if(weight_unit=="kg")
	{
		grams=value/1000;
	}
	else if(weight_unit=="oz")
	{
		grams=value/28.3495;
	}
	else if(weight_unit=="t")
	{
		grams=value/1000000;
	}
	else if(weight_unit=="μg")
	{
		grams=value*1000000;
	}
	else if(weight_unit=="mg")
	{
		grams=value*1000;
	}
	else if(weight_unit=="g")
	{
		return value;
	}
	return grams;
}
