function weightConversion(old_unit,new_unit,value)
{
	var grams=0;
	var newvalue=0;
	if(old_unit=="lb")
	{
		grams=value*453.592;
	}
	else if(old_unit=="stone")
	{
		grams=value*6350.28800006585;
	}
	else if(old_unit=="kg")
	{
		grams=value*1000;
	}
	else if(old_unit=="oz")
	{
		grams=value*28.3495;
	}


	if(new_unit=="lb")
	{
		newvalue=grams/453.592;
	}
	else if(new_unit=="stone")
	{
		newvalue=grams/6350.28800006585;
	}
	else if(new_unit=="kg")
	{
		newvalue=grams/1000;
	}
	else if(new_unit=="oz")
	{
		newvalue=grams/28.3495;
	}
	return newvalue;

}
function heightConversion(old_unit,new_unit,value)
{
	if(old_unit=="in")
	{
		cm=value*2.54;
	}
	else if(old_unit=="ft")
	{
		cm=30.48*value;
	}
	else if(old_unit=="m")
	{
		cm=100*value;
	}
	else if(old_unit=='cm')
	{
		cm=value;
	}
	//Now convert the standard value to newvalue accordingly
	if(new_unit=="m")
	{
		newvalue=cm/100;
	}
	else if(new_unit=="in")
	{
		newvalue=cm/2.54;
	}
	else if(new_unit=="ft")
	{
		newvalue=cm/30.48;
	}
	else if(new_unit=="cm")
	{
		return cm;
	}
	return newvalue;

}
function selectUnits(event,id)
{
	var unit=event.target.innerHTML;
	var spli=unit.split("(");
	var real_unit=spli[1].split(")");
	$("#"+id).html(real_unit[0]);
}
function construct_units(purpose,functioncall,type,id)
{
	var ans_arr;
	var date_arr=["Year(yr)","Months(M)","Days(d)"];
	var volume_arr=["cubic millimeter(mm³)","cubic meter(m³)","cubic kilometer(km³)","cubic inch(in³)","cubic foot(ft³)","cubic centimeter(cm³)","litre(L)"];
	var length_arr=["millimeter(mm)", "centimeter(cm)", "meter(m)", "kilometer(km)", "inch(in)", "feet/foot(ft)", "yard(yd)"];
	var area_arr=["square millimeter(mm²)","square meter(m²)","square kilometer(km²)","square inch(in²)","square foot(ft²)","square centimeter(cm²)"];
	var density_arr=["kg per cubic meter(kg/m³)","kg per liter(kg/l)","grams per milliliter(g/ml)","grams per liter(g/l)","pound per cubic inch(lb/in³)","pound per cubic feet(lb/ft³)","ton per cubic meter(t/m³)"];
	var weight_arr=["kilogram(kg)","ton(t)","gram(g)","ounces(oz)","pounds(lb)","milligrams(mg)","micrograms(μg)"];
	var temperature_arr=["Celsius(C)","Fahrenheit(F)" ,"Kelvin(K)","Rankine(ran)"];
	var velocity_arr=["(m/s)","(m/min)","(km/hr)","(yard/s)","(ft/s)","(mile/hr)"];
	var pressure_arr=["Pascal(Pa)","Bar(bar)","Atmosphere(atm)"];
	var gravity_arr=["(m²/s)","(m²/min)","(m²/hour)","(ft²/s)","(stokes)","(ft²/hr)","(m²/ms)"];
	var time_arr=["Seconds(sec)","Minutes(min)","Hours(hrs)","day(day)","weeks(weeks)","months(mon)","Years(yrs)"];

	functioncall="selectUnits(event,'"+id+"');"+functioncall;
	if(type=="date")
	{
		ans_arr=date_arr;
	}
	else if(type=="gravity")
	{
		ans_arr=gravity_arr;
	}
	else if(type=="time")
	{
		ans_arr=time_arr;
	}
	else if(type=="pressure")
	{
		ans_arr=pressure_arr;
	}
	else if(type=="velocity")
	{
		ans_arr=velocity_arr;
	}
	else if(type=="temperature")
	{
		ans_arr=temperature_arr;
	}
	else if(type=="length")
	{
		ans_arr=length_arr;
	}
	else if(type=="area")
	{
		ans_arr=area_arr;
	}
	else if(type=="volume")
	{
		ans_arr=volume_arr;
	}
	else if(type=="density")
	{
		ans_arr=density_arr;
	}
	else if(type=="weight")
	{
		ans_arr=weight_arr;
	}
	for(var i=0;i<ans_arr.length;i++)
	{
		var li=document.createElement("li");
		var a=document.createElement("a");
		$(a).attr("onclick",functioncall);
		$(a).html(ans_arr[i]);
		$(li).append(a);
		$("[purpose="+purpose+"]").append(li);
	}

}

function convToTime(value,unit)
{
		if(unit=="sec")
		{
			return value*1;
		}
		else if(unit=="yrs")
		{
			return value/(60*60*24*365);
		}
		else if(unit=="mon")
		{
			return value/(60*60*24*31);
		}
		else if(unit=="weeks")
		{
			return value/(60*60*24*7);
		}
		else if(unit=="day")
		{
			return value/(60*60*24);
		}
		else if(unit=="hrs")
		{
			return value/(60*60);
		}
		else if(unit=="min")
		{
			return value/60;
		}
}
