{
	if(unit=="sec")
	{
		return value*1;
	}
	else if(unit=="yrs")
	{
		return value*60*60*24*365;
	}
	else if(unit=="mon")
	{
		return value*60*60*24*31;
	}
	else if(unit=="weeks")
	{
		return value*60*60*24*7;
	}
	else if(unit=="day")
	{
		return value*60*60*24;
	}
	else if(unit=="hrs")
	{
		return value*60*60;
	}
	else if(unit=="min")
	{
		return value*60;
	}
}
function convertToLength(value,unit)
{
	if(unit=="mm")
	{
		return value*1000;
	}
	else if(unit=="cm")
	{
		return value*100;
	}
	else if(unit=="m")
	{
		return value/1;
	}
	else if(unit=="km")
	{
		var ans=value*0.0001;
		return ans;
	}
	else if(unit=="in")
	{
		return value*39.3701;
	}
	else if(unit=="ft")
	{
		return value*3.28084;
	}
	else if(unit=="yd")
	{
		return value*1.093613;
	}
	return value;
}
function convertToStandardLength(value,unit)
{
	if(unit=="mm")
	{
		return value/10;
	}
	else if(unit=="cm")
	{
		return value*1;
	}
	else if(unit=="m")
	{
		return value/0.01;
	}
	else if(unit=="km")
	{
		var ans=value/0.00001;
		return ans;
	}
	else if(unit=="in")
	{
		return value/0.393701;
	}
	else if(unit=="ft")
	{
		return value/0.0328084;
	}
	else if(unit=="yd")
	{
		return value/0.01093613;
	}
	return value;
}
function convertToUnitArea(area,unit)
{

	if(unit=="m²")
	{
		return area*0.0001;
	}
	else if(unit=="mm²")
	{
		return area*100;
	}
	else if(unit=="cm²")
	{
		return area/1;
	}
	else if(unit=="km²")
	{
		var ans=area*0.0000000001;
		return ans;
	}
	else if(unit=="ft²")
	{
		return area*0.00107639;
	}
	else if(unit=="in²")
	{
		return area*0.1550003;
	}
	return area;
}
function convertToUnitVolume(vol,unit)
{
	if(unit=="m³")
	{
		return vol*0.00000001;
	}
	else if(unit=="mm³")
	{
		return vol*1000;
	}
	else if(unit=="cm³")
	{
		return vol/1;
	}
	else if(unit=="L")
	{
		return vol*0.001;
	}
	else if(unit=="km³")
	{
		var ans=vol*0.000000000000001;
		return ans;
	}
	else if(unit=="ft³")
	{
		return vol*0.000035314615458165735;
	}
	else if(unit=="in³")
	{
		return vol*0.0610237;
	}
	return vol;
}
function convertToVelocity(velocity,unit)
{
	if(unit=="m/s")
	{
		velocity=velocity*1;
	}
	else if(unit=="m/min")
	{
		velocity=velocity*60;
	}
	else if(unit=="km/hr")
	{
		velocity=velocity*3.6;
	}
	else if(unit=="yard/s")
	{
		velocity=velocity*1.09361
	}
	else if(unit=="ft/s")
	{
		velocity=velocity*3.28084;
	}
	else if(unit=="mile/hr")
	{
		velocity=velocity*2.23694;
	}
	return velocity;
}

