function convertToStandardDensity(density,unit)
{
	if(unit=="kg/m³")
	{
		return density*100;
	}
	else if(unit=="kg/l")
	{
		return density/0.00001;
	}
	else if(unit=="g/ml")
	{
		return density/0.00001;
	}
	else if(unit=="g/l")
	{
		return density*100;
	}
	else if(unit=="lb/in³")
	{
		return density/0.00000036125
	}
	else if(unit=="lb/ft³")
	{
		return density/0.00062424
	}
	else if(unit=="t/m³")
	{
		return density/0.00001;
	}
}
function convertToTime(id)
{
	var value=$("#"+id).val();
	var am_or_pm=value.split(" ")[1];
	var time=value.split(" ")[0].split(" ")[0].split(":");
	var hour=parseInt(time[0]);
	var minute=parseInt(time[1]);
	if(hour <=12 && minute <=59)
	{
		$("#"+id).closest("div").removeClass("has-error");
	}
	else
	{
		$("#"+id).closest("div").addClass("has-error");
		return;
	}
	if(am_or_pm.includes("am") || am_or_pm.includes("pm"))
	{
		$("#"+id).closest("div").removeClass("has-error");
	}
	else
	{
			$("#"+id).closest("div").addClass("has-error");
			return;
	}
	var time={};
	time.hour=hour;
	time.minute=minute;
	time.ampm=am_or_pm;
	return time;
}
function convertToStandardHour(hour,ampm)
{
	var hr=0;
	if(ampm=="am")
	{
		if(hour==12)
		{
			hr=0;
		}
		else
		{
			hr=hour;
		}
	}
	else if(ampm=="pm")
	{
		if(hour==12)
		{
			hr=12;
		}
		else
		{
			hr=hour+12;
		}
	}
	return hr;
}
function getFormatTime(format_hr,min)
{
	var fr=format_hr+":"+min+":00";
	var str='March 14, 1996 '+fr;
	var d=new Date(str);
	return d;
}
function addMinutes(date, minutes)
{
    return new Date(date.getTime() + minutes*60000);
}
function convertToNormalTime(dat)
{
var hours = dat.getHours();
var minutes=dat.getMinutes();
var mid="am";
if(hours>12)
{
hours=hours%12;
mid="pm";
}else if(hours==12){
	hours = hours;
	mid = "pm"
}

var ges={};
if(hours<10)
{
if(hours==0)
{
hours=12;
}
else {
hours="0"+hours;
}

}
if(minutes<10)
{
minutes="0"+minutes;
}
ges.hours=hours;
ges.minutes=minutes;
ges.ampm=mid;
return ges;
}
